mapper.select:
        select n.*,c.categoryName from t_news as n,t_category as c 

		order by publishTime desc
		limit #{startRows},#{pageSize}

mapper.update:
update t_news
publishTime=#{publishTime}, publishStatus=#{publishStatus}, title=#{title}, contentTitle=#{contentTitle}, content=#{content}, contentAbstract=#{contentAbstract}, keywords=#{keywords}, author=#{author}, categoryId=#{categoryId}
where newsId=#{newsId}
